
public class Ex01 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for (int i = 0; i < 10; i++) {
			System.out.print(i);
		}

		System.out.println();
		for (int i = 9; i >= 0; i--) {
			System.out.print(i);
		}

		System.out.println();
		for (int i = 9; i >= 0; i -= 2) {
			System.out.print(i);
		}
	}

}
